mapper.insert:
INSERT INTO sf_menu
( name
, english_name
, parents_id
, url
, is_used
, sort_sequence
, role_id
, icon_class
, created_by
, created_date
, last_modified_by
, last_modified_date)
VALUES

  ( #{item.name}
  , #{item.englishName}
  , #{item.parentsId}
  , #{item.url}
  , #{item.isUsed}
  , #{item.sortSequence}
  , #{item.roleId}
  , #{item.iconClass}
  , #{item.createdBy}
  , #{item.createdDate}
  , #{item.lastModifiedBy}
  , #{item.lastModifiedDate})

update.foreach:
UPDATE sf_menu
SET name               = #{item.name}
  , english_name       = #{item.englishName}
  , parents_id         = #{item.parentsId}
  , url                = #{item.url}
  , is_used            = #{item.isUsed}
  , sort_sequence      = #{item.sortSequence}
  , role_id            = #{item.roleId}
  , icon_class         = #{item.iconClass}
  , last_modified_by   = #{item.lastModifiedBy}
  , last_modified_date = #{item.lastModifiedDate}
WHERE id = #{item.id}

UPDATE sf_menu
SET  role_id = #{item.roleId}
, last_modified_by   = #{item.lastModifiedBy}
, last_modified_date = #{item.lastModifiedDate}
WHERE parents_id = #{item.id}

